command.operation:
config router static
	edit {$params.object_id}
		{if $params.ip_dest neq ""}
		set dst {$params.ip_dest} {$params.mask}
		{else}
		set dst 0.0.0.0 0.0.0.0
		{/if}
		{if $params.distance neq ""}
		set distance {$params.distance}
		{else}
		set distance 100
		{/if}
		{if $params.priority neq ""}
		set priority {$params.priority}
		{else}
		set priority 10
		{/if}
		set device "{$params.interface}"
		{if $params.comment neq ""}
		set comment "{$params.comment}"
		{/if}
		{if $params.gateway neq ""}
        set protocol {$params.gateway}
    	{/if}
	next 
end

config router static
	edit {$params.object_id}
		set dst {$params.ip_dest} {$params.mask}
		set distance {$params.distance}
		set priority {$params.priority}
		set device "{$params.interface}"
		set comment "{$params.comment}"
	next 
end

{if mb_check_encoding($params.comment, 'ASCII') == false}INPUT RESTRICTION. Multibyte characters not allowed, invalid comment.
{/if}
{if strpos($params.comment, '$') !== false}INPUT RESTRICTION. Invalid comment.
{/if}
